offers.example:
"offers": [
    //Early bird tickets
    {
        "@type": "Offer",
        "name": "Early Bird Tickets",
        "priceValidUntil": "2019-02-04T09:00:00",
        "price": "50.00",
        "priceCurrency": "GBP"
    },

    //Academic Pricing
    {
      "@type": "Offer",
      "name": "Academic Discount",
      "price": "100.00",
      "priceValidUntil": "2019-02-05T17:00:00",
      "eligibleCustomerType": {
        "@type": "BusinessEntityType",
        "name": "Academic"
      },
      "priceCurrency": "GBP",
      "url": "http://www.eventbrite.com/my-workshop/academic"
    },
    //Industry Pricing
    {
      "@type": "Offer",
      "name": "Industry Price",
      "price": "500.00",
      "priceCurrency": "GBP",
        "eligibleCustomerType": {
        "@type": "BusinessEntityType",
        "name": "Industrial"
      },
      "url": "http://www.eventbrite.com/my-workshop/industry"
    },
    //First 50 tickets free
    {
      "@type": "Offer",
      "name": "Free bursary for first 50 registrants",
      "price": "0.00",
      "priceCurrency": "GBP",
      "url": "http://www.eventbrite.com/my-workshop",
      "eligibleQuantity": {
        "@type": "QuantitativeValue",
        "value": 50
      }
    }
]

