description.example:
{
    "@type": ["Phenotype"],
    "description": "any structural anomaly of the fingers or toes"
}

identifier.example:
//Using a text
{
    "@type": ["Phenotype"],
    "identifier": "MP:0002110"
}
//Using a URL directly (looks like a text)
{
    "@type": ["Phenotype"],
    "identifier": "http://purl.obolibrary.org/obo/MP_0002110"
}
//Using a URL modeled as schema:URL (equivalent to the previous one but more verbose)
{
    "@type": ["Phenotype"],
    "identifier": {
      "@type": "URL",
      "url": "http://purl.obolibrary.org/obo/MP_0002110"
    }
}

image.example:
//Using a URL directly (looks like a text)
{
  "@type": ["Phenotype"],
  "image": "http://www.ebi.ac.uk/mi/impc/phenotype-archive/media/images/900/M00571072_00021593_download_tn_large.jpg"
}

mainEntityOfPage.example:
//Preferred way from DataRecord
{

  "@type": "DataRecord",
  "@id": "http://www.mousephenotype.org/data/phenotypes/MP:0002110"
  "mainEntity": {
    "@type": ["Phenotype"],
    "name": "Name and any other property for this Phenotype entity"
  }
}

//Also possible from Phenotype
{
  "@type": ["Phenotype"],
  "mainEntityOfPage": { "@id": "http://www.mousephenotype.org/data/phenotypes/MP:0002110" }
}
