Enablesmf.example:
//Gene enabling a GO molecular function
{
  "@type": ["Gene"],
  "enablesMF": {
    "@type": "CategoryCode",
    "url": "http://purl.obolibrary.org/obo/GO_0000166",
    "codeValue": "GO:0000166",
    "name": "nucleotide binding"
    }
}

Identifier.example:
//Using a text
{
    "@type": ["Gene"],
    "identifier": "ENSG00000142192"
}
//Using a URL directly (looks like a text)
{
    "@type": ["Gene"],
    "identifier": "http://identifiers.org/ensembl:ENSG00000142192"
}
//Using a URL modeled as schema:URL (equivalent to the previous one but more verbose)
{
    "@type": ["Gene"],
    "identifier": {
      "@type": "URL",
      "url": "http://identifiers.org/ensembl:ENSG00000142192"
    }
}

Image.example:
//Using a URL directly (looks like a text)
{
  "@type": ["Gene"],
  "image": "https://en.wikipedia.org/wiki/Amyloid_precursor_protein#/media/File:Ideogram_human_chromosome_21.svg"
}

Involvedinbp.example:
//Gene involved in a GO biological process
{
  "@type": ["Gene"],
  "involvedInBP": {
    "@type": "CategoryCode",
    "url": "http://purl.obolibrary.org/obo/GO_0000278",
    "codeValue": "GO:0000278",
    "name": "mitotic cell cycle"
    }
}

Ispartofbiochementity.example:
//Is contained in a chromosome in positions X to Y
{
  "@type": ["Gene"],
  "isPartOfBioChemEntity": {
    "@type": "BioChemEntity",
    "name": "Chromosome 21",
  }
}
//Is contained in an organism
{
  "@type": ["Gene"],
  "isPartOf": {
    "@type": "BioChemEntity",
    "name": "Homo sapiens",
    "alternateName": "Human",
    "codeCategory": {
      "@type": "CategoryCode",
      "codeValue": "9606",
      "url": "http://purl.bioontology.org/ontology/NCBITAXON/9606",
      "sameAs": "http://purl.uniprot.org/taxonomy/9606",
      "inCodeSet": {
        "@type": "CategoryCodeSet",
        "name": "NCBI taxon"
      } 
    }
  }
}

Mainentityofpage.example:
//Preferred way from DataRecord
{

  "@type": "DataRecord",
  "@id": "http://rdf.ebi.ac.uk/resource/ensembl/ENSG00000142192"
  "mainEntity": {
    "@type": ["Gene"],
    "name": "Name and any other property for this Gene entity"
  }
}

//Also possible from Gene
{
  "@type": ["Gene"],
  "mainEntityOfPage": { "@id": "http://rdf.ebi.ac.uk/resource/ensembl/ENSG00000142192" }
}
